mapper.select:
select

  distinct

'false' as QUERYID,

from ord_od_person_info




  order by ${orderByClause}


  limit #{limitStart} , #{limitEnd}

mapper.insert:
insert into ord_od_person_info (PERSON_ID, ORDER_ID, OPER_TYPE, 
  INTERPER_ID, INTERPER_NAME, TEL, 
  INTERPER_FEE, CREART_TIME, REMARK, 
  STEP, RECEIVE_STATE, EXPECT_END_TIME
  )
values (#{personId,jdbcType=BIGINT}, #{orderId,jdbcType=BIGINT}, #{operType,jdbcType=VARCHAR}, 
  #{interperId,jdbcType=VARCHAR}, #{interperName,jdbcType=VARCHAR}, #{tel,jdbcType=VARCHAR}, 
  #{interperFee,jdbcType=BIGINT}, #{creartTime,jdbcType=TIMESTAMP}, #{remark,jdbcType=VARCHAR}, 
  #{step,jdbcType=VARCHAR}, #{receiveState,jdbcType=VARCHAR}, #{expectEndTime,jdbcType=TIMESTAMP}
  )
